    public static IBaseTrait EnhancedEnergySiphonTrait { get; } = new BaseTrait(475);
    /// <summary>
    /// <see href="https://garlandtools.org/db/#action/50476"><strong>Ruin Mastery III</strong></see> (SMN) [476]
