/// <summary>
/// <see href="https://na.finalfantasyxiv.com/jobguide/sage"><strong>Sage</strong></see>
/// <br>Number of Actions: 45</br>
/// <br>Number of Traits: 12</br>
/// </summary>
[Jobs(Job.SGE)]
public abstract partial class SageRotation : CustomRotation
{
