    /// <para>Upgrades Fuga to Fuko.</para>
    /// </summary>
    public static IBaseTrait FugaMasteryTrait { get; } = new BaseTrait(519);
    /// <summary>
    /// <see href="https://garlandtools.org/db/#action/50520"><strong>Way of the Samurai</strong></see> (SAM) [520]
